import React from 'react';
import fire from './fire';


class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ref_value: 0,
            ref_code: "",
            handleLogout: props.handleLogout,
            user: props.user
        };
        // const { handleLogout, user } = props;
    }

    componentDidMount() {
        var ref = fire.database().ref("user");
        console.log(this.state.user.email);
        ref.orderByChild("email").equalTo(this.state.user.email).on("child_added", function (snapshot) {
            console.log(snapshot.val().referrals);
            console.log(snapshot.val().code);
            // this.setState( {
            //     ref_value : snapshot.val().referrals,
            //     ref_code : snapshot.val().code
            // });
        });
        this.setState( {
                ref_value : snapshot.val().referrals,
                ref_code : snapshot.val().code
            });
    };

    render () {
        return (
            <section className="hero">
            {console.log("in return for Hero")}
            <nav>

                <h2>Welcome {this.state.user.email}</h2>


                <button onClick={this.state.handleLogout}>Logout</button>
            </nav>

            <h1>Number of Referrals: {this.state.ref_value}</h1>
            <h1>Your Referral Code: {this.state.ref_code}</h1>

            <label><h1>Share with Email:</h1></label>
            {/* TRY TO MAKE IT ADD THE REFERRAL CODE TO EMAIL */}
            <a href="mailto:?subject=Check Out The TakeOff! &amp;body=Sign up for the TakeOff and use my referral code" title="Share by Email">
                <img src="http://png-2.findicons.com/files/icons/573/must_have/48/mail.png" />
            </a>
        </section>

        //ADD PRIZE DIVS HERE
        );
    };

}

export default Hero;

