import React from 'react';
import fire from './fire';


export class Root extends React.Component {


    emails(){

        var emailList = [];

        var ref = fire.database().ref("emails/email");


        ref.on("value", (snapshot) => {
          console.log(snapshot.val());
          console.log(emailList.indexOf(snapshot.val()));
          if (emailList.indexOf(snapshot.val()) === -1)
          {
            emailList.push(snapshot.val());
          }
        });
        return emailList;
    }

    // printEmails() {
    //     var emails = this.emails();
    //     var printEmails = "";
    //     for (i=0; i < emails.length; i++)
    //     {

    //     }
    // }

    testConLog() {
        console.log("this is a test");
    }

    render () {
        this.testConLog();
        var emails = this.emails();
        for (var i=0; i < emails.length; i++)
        {
            var p = document.createElement("p");
            p.innerHTML = emails[i];
            document.getElementById("rootDiv").appendChild(p);
        }
        return (
            <div id="rootDiv2">
            </div>
        );
    }
}

export default Root;
