import React from 'react';
import fire from './fire';
import Prizes from './Prizes';


class Hero extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            ref_value: 0,
            ref_code: "",
            handleLogout: props.handleLogout,
            user: props.user
        };
        this.setVariables = this.setVariables.bind(this);
        // const { handleLogout, user } = props;
    }

    setVariables(num, code) {
        console.log(num);
        this.setState({
            ref_value: num,
            ref_code: code
        });
    }

    componentDidMount() {
        var ref = fire.database().ref("user");
        console.log(this.state.user.email);
        var refNums = 999;
        var refCode = "error";
        // var stateData = [];
        ref.orderByChild("email").equalTo(this.state.user.email).on("child_added", (snapshot) => {
            refNums = snapshot.val().referrals;
            refCode = snapshot.val().code;
            console.log(refCode);
            this.setVariables(refNums, refCode);
            // let data = [];
            // data.push(refNums);
            // data.push(refCode);
            // stateData = await Promise.all(data); 
        });

        // this.setState( {
        //     ref_value : refNums,
        //     ref_code : refCode
        //     });
    };

    render() {
        var forHref = 'mailto:?subject=Check Out The Takeoff! &amp;body=Sign up for The Takeoff and use my referral code ' + this.state.ref_code;
        return (
            <div className="container-fluid">
                <section className="hero">
                    {console.log("in return for Hero")}
                    <nav className="nav row">
                        <div className="col-3 justify-content-center">
                            <h2 className="d-flex justify-content-left">Welcome {this.state.user.email}</h2>
                        </div>
                        <div className="col-7"></div>
                        <div className="col-2 pt-4">
                            <button className="btn btn-danger" onClick={this.state.handleLogout}>Logout</button>
                        </div>
                    </nav>
                    <hr />

                    <div className="text-center">
                        <h1 className="d-inline">Your Referral Code: </h1>
                        <h1 className="d-inline refCode">{this.state.ref_code}</h1>
                    </div>
                    {/* <h1>You have referred {this.state.ref_value} people.</h1> */}

                    {/* <label><h1>Share with Email:</h1></label>
                    <a href={forHref} title='Share by Email'>
                        <img src="http://png-2.findicons.com/files/icons/573/must_have/48/mail.png" alt="mail image" />
                    </a> */}
                    <br />
                    <div className="row">
                        <div className="col-3"></div>
                    <div className="border border-light prizes col-5">
                    <h3>You have referred {this.state.ref_value} people. Your Prizes:</h3>
                    <Prizes refers={this.state.ref_value} />
                    </div>
                    <div className="col-3"></div>
                    </div>

                </section>
            </div>

            //ADD PRIZE DIVS HERE
        );
    };

}

export default Hero;

