import React from 'react';
import fire from './fire'



const Hero = (props) =>{

    const {handleLogout, user} = props;

    console.log(user.email);

    var ref_value=0;
    var ref_code='';
    var ref = fire.database().ref("user");
    // ref.on("value", function(snapshot) {
    //     console.log(snapshot.val());
    //   });

    //EXECUTES AFTER RETURN--NEEDS TO BE FIXED
    ref.orderByChild("email").equalTo(user.email).on("child_added", function(snapshot) {
      console.log(snapshot.val().referrals);
      ref_value=snapshot.val().referrals;
      console.log(snapshot.val().code);
      ref_code=snapshot.val().code;
      //var referrals=snapshot.val().referrals;
    });

    return(
        <section className="hero">
            {console.log("in return")}
            <nav>

            <h2>Welcome {user.email}</h2>


            <button onClick={handleLogout}>Logout</button>
            </nav>    

            <h1>Number of Referrals: {ref_value}</h1>
            <h1>Your Referral Code: {ref_code}</h1>

            <label><h1>Share with Email:</h1></label>
            {/* TRY TO MAKE IT ADD THE REFERRAL CODE TO EMAIL */}
            <a href="mailto:?subject=Check Out The TakeOff! &amp;body=Sign up for the TakeOff and use my referral code" title="Share by Email">
            <img src="http://png-2.findicons.com/files/icons/573/must_have/48/mail.png"/>
            </a>
        </section>   

        //ADD PRIZE DIVS HERE
    );

};
export default Hero;
